    public List<List<Integer>> threeSum(int[] nums) {

        if(nums.length < 3)
            return new ArrayList<>();

        Map keys = new HashMap();
        List list = new ArrayList();

        for (int i = 0; i < nums.length - 1; i++) {
            Map map = new HashMap();
            for (int j = i + 1; j < nums.length; j++) {
                int result = 0 - (nums[i] + nums[j]);
                if (map.containsKey(result)) {
                    List l = new ArrayList();
                    l.add(result);
                    l.add(nums[i]);
                    l.add(nums[j]);

                    if(result == 0 && nums[i] == 0 && nums[j] == 0){
                        String key = "000";
                        if (!keys.containsKey(key)) {
                            keys.put(key, 0);
                            list.add(l);
                        }
                        continue;
                    }

                    String key = getKey(l);
                    if (!keys.containsKey(key)) {
                        keys.put(key, 0);
                        list.add(l);
                    }

                } else {
                    map.put(nums[j], 0);
                }
            }
        }

        return list;
    }

    private String getKey(List l) {
        Collections.sort(l);
        String s = "";
        for (Object o : l) {
            s = s + o;
        }
        return s;
    }


    private Object[] mergeResult(int r, List<Integer> k) {
        List l = new ArrayList();
        String key = "";
        boolean rAdded = false;
        for (Integer i : k) {
            if (i < r) {
                l.add(i);
                key = key + i;
            } else {
                if (!rAdded) {
                    l.add(r);
                    rAdded = true;
                    key = key + r;
                }
                l.add(i);
                key = key + i;

            }
        }
        if (!rAdded) {
            l.add(r);
            key = key + r;
        }
        return new Object[]{l, key};
    }

    private List<List<Integer>> bruteForce(int[] nums) {
        int firstPointer = 0;
        int secondPointer = 0;

        List mainList = new ArrayList();

        for (int i = 0; i < nums.length - 2; i++) {
            for (int j = i + 1; j < nums.length - 1; j++) {
                for (int k = j + 1; k < nums.length; k++) {
                    if (nums[i] + nums[j] + nums[k] == 0) {
                        List list = new ArrayList();
                        list.add(nums[i]);
                        list.add(nums[j]);
                        list.add(nums[k]);
                        if (!containsTriplet(mainList, list)) {
                            mainList.add(list);
                        }
                    }
                }
            }

        }

        return mainList;
    }

    private boolean containsTriplet(List<List<Integer>> mainList, List<Integer> list) {
        for (List elements : mainList) {
            if (isEqual(elements, new ArrayList<>(list))) {
                return true;
            }
        }
        return false;
    }

    private boolean isEqual(List<Integer> elements, List<Integer> list) {

        for (Integer e : elements) {
            if (list.contains(e)) {
                list.remove((Object) e);
                continue;
            } else {
                return false;
            }
        }

        return true;
    }

